package com.peter.wagstaff.jddemo.activities.fragments;import android.app.Activity;import android.app.AlertDialog;import android.view.View;import android.widget.Button;import com.peter.wagstaff.jddemo.R;public abstract class ConfirmOnlyDialog {    AlertDialog dialog;    public ConfirmOnlyDialog(Activity activity, String message) {        final AlertDialog.Builder builder = new AlertDialog.Builder(activity);        View customLayout = activity.getLayoutInflater().inflate(R.layout.confirm_only_dialog, null);        Button yesButton = customLayout.findViewById(R.id.yes_button);        builder.setView(customLayout);        builder.setCancelable(true);        yesButton.setText(message);        yesButton.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                onPositiveClicked();                dialog.dismiss();            }        });        dialog = builder.create();    }    abstract public boolean onPositiveClicked();    public void show() {        dialog.show();    }}