package com.peter.wagstaff.jddemo.activities.fragments;import android.app.AlertDialog;import android.content.Context;import android.content.DialogInterface;import android.text.InputFilter;import android.widget.EditText;import android.widget.Toast;public abstract class InputDialog {    AlertDialog dialog;    EditText input;    public InputDialog(final Context context, String title, String message, String hint, final String errorMessage) {        input = new EditText(context);        input.setHint(hint);        AlertDialog.Builder builder = new AlertDialog.Builder(context);        builder.setTitle(title);        builder.setMessage(message);        builder.setView(input);        builder.setPositiveButton("Enter", new DialogInterface.OnClickListener() {            @Override            public void onClick(DialogInterface dialog, int which) {                if(onPositiveClicked(input.getText().toString())) {                    input.setText("");                    dialog.dismiss();                }                else {                    if(!errorMessage.equals("")) {                        Toast.makeText(context, errorMessage, Toast.LENGTH_SHORT).show();                    }                }            }        });        builder.setNegativeButton("Cancel", new DialogInterface.OnClickListener() {            @Override            public void onClick(DialogInterface dialog, int which) {                onNegativeClicked();                input.setText("");                dialog.dismiss();            }        });        dialog = builder.create();    }    public InputDialog(Context context, String title, String message, String hint, String errorMessage, InputFilter inputFilter) {        this(context, title, message, hint, errorMessage);        input.setFilters(new InputFilter[]{inputFilter});    }    public InputDialog(Context context, String title, String message, String hint, String errorMessage, int inputType) {        this(context, title, message, hint, errorMessage);        input.setInputType(inputType);    }    public void onNegativeClicked() {}    abstract public boolean onPositiveClicked(String userInput);    public void show() {        dialog.show();    }}